package unidad1;

public class Actividad1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//variables
	int contador = 10;
		
		
		// Enteros
		 byte b = 10;
		 short s = 200;
		 int edad = 25;
		 long poblacion = 47000000L;
		 // Decimales
		 float precio = 9.99f;
		 double pi = 3.1415926535;
		 // Carácter
		 char inicial = 'A';
		 // Booleano
		 boolean aprobado = true;
		 
// Operaciones para contador
		 int resultado= contador-3;
		 
		 
		 
//para imprimir por consola		 
		 System.out.println("byte: rango [-128 a 127]" + b);
		 System.out.println("short: rango [-32.768 a 32.767]  " + s);
		 System.out.println("Edad: " + edad);
		 System.out.println("Población: " + poblacion);
		 System.out.println("Precio: " + precio);
		 System.out.println("PI: " + pi);
		 System.out.println("Inicial: " + inicial);
		 System.out.println("¿Aprobado? " + aprobado);
		 System.out.println("Resultado de restar 3 al contador " + resultado);
		
		
		
		
		
		
		
		

	}

}
